const http = require("http");
const fs = require("fs");

module.exports = function generator() {
    const file = "http://wdllhj.top/gitignore.txt";
    http.get(file, (res) => {
        res.setEncoding("utf8");
        let data = '';
        res.on("data", (chunk) => {
            data += chunk;
        })
        res.on("end", () => {
            fs.writeFileSync('./.gitignore', data);
        })

        res.on("error", (err) => {
            console.log(err);
        })
    })
}
